// Function to print the Tic Tac Toe board
void printBoard(const std::vector<std::vector<char>>& board) {
    for (int i = 0; i < 3; ++i) {
        for (int j = 0; j < 3; ++j) {
            std::cout << board[i][j] << ' ';
        }
        std::cout << '\n';
    }
}

// Function to check if a player has won
bool checkWin(const std::vector<std::vector<char>>& board, char c) {
    // Check rows and columns
    for (int i = 0; i < 3; ++i) {
        if ((board[i][0] == c && board[i][1] == c && board[i][2] == c) ||
            (board[0][i] == c && board[1][i] == c && board[2][i] == c)) {
            return true;
        }
    }

    // Check diagonals
    if ((board[0][0] == c && board[1][1] == c && board[2][2] == c) ||
        (board[0][2] == c && board[1][1] == c && board[2][0] == c)) {
        return true;
    }

    return false;
}

int main() {
    std::vector<std::vector<char>> board(3, std::vector<char>(3, '-'));
    int moves = 0;

    while (true) {
        printBoard(board);
        int x, y;
        if (moves % 2 == 0) {
            std::cout << "Player 1's turn. Enter row and column: ";
            std::cin >> x >> y;
            board[x][y] = 'X';
            if (checkWin(board, 'X')) {
                std::cout << "Player 1 wins!\n";
                break;
            }
        } else {
            std::cout << "Player 2's turn. Enter row and column: ";
            std::cin >> x >> y;
            board[x][y] = 'O';
            if (checkWin(board, 'O')) {
                std::cout << "Player 2 wins!\n";
                break;
            }
        }

        ++moves;
        if (moves == 9) {
            std::cout << "It's a draw.\n";
            break;
        }
    }

    return 0;
}

