std::string select_random_word(const std::string& filename) {
    std::ifstream file(filename);
    if (!file.is_open()) {
        throw std::runtime_error("Could not open file: " + filename);
    }

    std::vector<std::string> words;
    std::string word;
    while (file >> word) {
        words.push_back(word);
    }
    file.close();

    if (words.empty()) {
        throw std::runtime_error("No words found in the file");
    }

    return words[rand() % words.size()];
}

bool is_letter_in_word(char letter, const std::string& word) {
    return std::find(word.begin(), word.end(), letter) != word.end();
}

int main() {
    srand(time(nullptr));

    const std::string filename = "words.txt";
    std::string secret_word;
    try {
        secret_word = select_random_word(filename);
    } catch (const std::exception& e) {
        std::cerr << "Error: " << e.what() << std::endl;
        return 1;
    }

    int attempts_left = 7;
    std::string guessed_word(secret_word.length(), '_');
    std::string used_letters;

    while (attempts_left > 0 && guessed_word != secret_word) {
        std::cout << "Current state: " << guessed_word << std::endl;
        std::cout << "Attempts left: " << attempts_left << std::endl;
        std::cout << "Used letters: " << used_letters << std::endl;
        std::cout << "Enter your guess: ";

        char guess;
        std::cin >> guess;

        if (is_letter_in_word(guess, used_letters)) {
            std::cout << "You have already guessed this letter." << std::endl;
        } else {
            used_letters += guess;

            if (is_letter_in_word(guess, secret_word)) {
                for (size_t i = 0; i < secret_word.length(); ++i) {
                    if (secret_word[i] == guess) {
                        guessed_word[i] = guess;
                    }
                }
            } else {
                --attempts_left;
            }
        }
    }

    if (guessed_word == secret_word) {
        std::cout << "Congratulations! You've won. The word was: " << secret_word << std::endl;
    } else {
        std::cout << "Sorry, you lost. The word was: " << secret_word << std::endl;
    }

    return 0;
}

