#include <iostream>
#include <fstream>
#include <vector>
#include <algorithm>
#include <string>

struct Student {
    std::string name;
    int grade;

    bool operator<(const Student& other) const {
        return grade > other.grade;
    }
};

int main() {
    // Read students from input file
    std::ifstream inputFile("students.txt");
    if (!inputFile.is_open()) {
        std::cerr << "Error opening input file." << std::endl;
        return 1;
    }

    std::vector<Student> students;
    Student tempStudent;
    while (inputFile >> tempStudent.name >> tempStudent.grade) {
        students.push_back(tempStudent);
    }
    inputFile.close();

    // Sort students by grade (highest to lowest)
    std::sort(students.begin(), students.end());

    // Write sorted students to output file
    std::ofstream outputFile("sorted_students.txt");
    if (!outputFile.is_open()) {
        std::cerr << "Error opening output file." << std::endl;
        return 1;
    }

    for (const auto& student : students) {
        outputFile << student.name << " " << student.grade << std::endl;
    }
    outputFile.close();

    std::cout << "Students sorted successfully. Check 'sorted_students.txt' for the result." << std::endl;

    return 0;
}
