// Function to get all HTML files in a directory
function getHtmlFiles(dir, depth) {
  const entries = fs.readdirSync(dir, { withFileTypes: true });
  let htmlFiles = [];

  for (const entry of entries) {
    const fullPath = path.join(dir, entry.name);

    if (entry.isFile() && path.extname(entry.name) === '.html') {
      htmlFiles.push({ path: fullPath, depth });
    } else if (entry.isDirectory()) {
      htmlFiles = htmlFiles.concat(getHtmlFiles(fullPath, depth + 1));
    }
  }

  return htmlFiles;
}

// Function to generate the XML sitemap
function generateSitemap(files, baseUrl) {
  const urlset = xmlbuilder.create('urlset', { encoding: 'UTF-8' }).att('xmlns', 'http://www.sitemaps.org/schemas/sitemap/0.9');

  for (const file of files) {
    const url = urlset.ele('url');
    const relativeUrl = path.relative(__dirname, file.path).replace(/\\/g, '/');
    const fullUrl = `${baseUrl}/${relativeUrl}`;

    url.ele('loc', fullUrl);
    url.ele('priority', 1 - file.depth * 0.1);
  }

  return urlset.end({ pretty: true });
}

