package main

import (
	"bufio"
	"encoding/json"
	"fmt"
	"io/ioutil"
	"log"
	"os"
	"strings"
	"sync"
)

func main() {
	filePath := "large_text_file.txt"

	wordFrequency, err := countWordFrequency(filePath)
	if err != nil {
		log.Fatalf("Error counting word frequency: %v", err)
	}

	jsonData, err := json.Marshal(wordFrequency)
	if err != nil {
		log.Fatalf("Error marshaling JSON data: %v", err)
	}

	err = ioutil.WriteFile("word_frequency.json", jsonData, 0644)
	if err != nil {
		log.Fatalf("Error writing JSON file: %v", err)
	}
	fmt.Println("Word frequency saved to word_frequency.json")
}

func countWordFrequency(filePath string) (map[string]int, error) {
	file, err := os.Open(filePath)
	if err != nil {
		return nil, fmt.Errorf("error opening file: %w", err)
	}
	defer file.Close()

	scanner := bufio.NewScanner(file)
	wordFrequency := make(map[string]int)
	var wg sync.WaitGroup
	var mu sync.Mutex

	for scanner.Scan() {
		line := scanner.Text()
		wg.Add(1)

		go func(line string) {
			defer wg.Done()
			words := strings.Fields(line)

			mu.Lock()
			for _, word := range words {
				word = strings.ToLower(strings.Trim(word, ".,!?:;\"'"))
				wordFrequency[word]++
			}
			mu.Unlock()
		}(line)
	}

	wg.Wait()

	if err := scanner.Err(); err != nil {
		return nil, fmt.Errorf("error scanning file: %w", err)
	}

	return wordFrequency, nil
}
```

